const generateQRWithLib = (link, container) => {
    // Assuming qr-lib.min.js exposes a function like `QRcode.toCanvas()`
    QRcode.toCanvas(link, { width: 300 }, function(error, canvas) {
        if (error) {
            console.error(error);
        } else {
            container.appendChild(canvas);
        }
    });
};
